mapper.select:
    SELECT *   
	FROM (SELECT ROWNUM,   
	             m.*,    
	             FLOOR((ROWNUM - 1) / #{limit} + 1) page 
	      FROM (  
	             SELECT * FROM member_tbl   
	             ORDER BY member_id ASC  
	           ) m    
	      )     
	WHERE page = #{page}

SELECT * 
FROM (SELECT ROWNUM,   
             m.*,    
             FLOOR((ROWNUM - 1) / #{limit} + 1) page   
      FROM (  
             SELECT * FROM member_tbl   
             WHERE ${fld} ${expr}
             ORDER BY member_id ASC  
           ) m    
      )     
WHERE page = #{page}
